package Programs;

public class PrintStar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i,j,k;
           for(int i1=1;i1<=5;i1++)
           {
        	    for (int j1=1; j1>i1;j1--)
        	    {
        	    	System.out.println(" ");
        	    }
        	    for (int k1=1;k1<=1;k1++)
        	    {
        	    	System.out.println("*");
        	    }
           }
	}

}
